import React from 'react';

let message;
let account;

connectMetamask = async () => {  
    try {
      const accounts = await window.ethereum.request({ method: "eth_requestAccounts" });
      account = accounts[0];
      switchNetwork();
    } catch (err) {
      message = "Metamask: Failed to connect to Metamask...";
    }

    console.log(account[0]);
  };

  const switchNetwork = async () => {
    try {
      await window.ethereum.request({
        method: "wallet_switchEthereumChain",
        params: [{ chainId: "0x13881" }],
      });
      //chain id for polygon = "0x89"

      message = "Metamask: Succesfuly Connected to Metamask";
    } catch (switchError) {
      if (switchError.code === 4902) {
        message = "Metamask: Add polygon to your wallet";
      } else {
        message = "Metamask: Cannot switch to the network";
      }
    }
  };

export default connectMetamask;
