import { useState, useEffect} from "react";
import { useDispatch } from "react-redux";
import "./index.css";
const contractModel = require('./contractModel.js');

const Header = () => {
    const [button, setButton] = useState(1);

    let isConnected = useSelector((state) => state.isConnected.isConnected)
    const dispatch = useDispatch();

    const handleMetamask = () =>{
        if(button === 2){
            contractModel.disconnectToMetamask();
            setButton(1);
            dispatch.posts.actions.setIsConnected(false);
        }
        else{
            contractModel.connectToMetamask();
            setButton(2);
            dispatch.posts.actions.setIsConnected(true);

        }
    }



    useEffect(() => {}, [button]);

    return(
        <div className="header">
        <a href="/"className="menuLink">Home</a><a href="/mynfts" className="menuLink">My Nfts</a>
        <button onClick={handleMetamask} className="MetamaskBtn"><img src={`../UI/Button${button}.png`}/></button>
        </div> 
    );
}

export default Header;
