import { useState, useEffect } from "react";
import axios from 'axios';
import "./index.css";

//!add my nfts site!
//!make it look acceptable!

function App(){
    const [generated, setGenerated] = useState(false);
    const [message, setMessage] = useState("");
    const [nftParameters, setNftParameters] = useState("");
    const [imageLink, setImageLink] = useState("");

    useEffect(() => {console.log("useEffect")}, [imageLink,generated, message]);

    const generateNft = async () => {
        console.log("working");
        const link = await axios.get(`http://localhost:3001/?aiParams=${nftParameters}`);
        console.log("done");
        return(link.data);
    }

    const handleChange = (e) =>{
        console.log(e.target.value);
        setNftParameters(e.target.value);
    }


    const handleSubmit = () => {
        if(nftParameters == ""){
            setMessage("*At least 1 word*");
        }
        else{
            setMessage("*Generatig in progress...*")
            generateNft().then((data) => {
                console.log("xd");
                setImageLink(data); 
            });
            setGenerated(true);
            //setMessage("*failed to generate*");
        }
    }

    const handleMint = () => {
        //contract model mint()
    }

    if(!generated){
        return (
            <div>
                <p className="mainText">
                    Mint your ideas!
                </p> 
                <p className="subText">
                    Ai generated Nfts
                </p>
                <div className="form">
                    <input onChange={handleChange} className="textInput" hidden="daa"></input>
                    <button onClick={handleSubmit} style={{}}><img src="../UI/button2.png"/></button>
                </div>
            </div>
            );
    }
    else{
        return(
            <div>
                <p>Your ai generatedNft is ready</p>
                <img src={imageLink}/>
                <button onClick={handleMint()}><img src="../UI/Button3.png"/></button>
            </div>
        );

    }

}

export default App;
