import { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import "./index.css";
const contractModel = require("./contractModel.js");


function MyNfts (){
    let listOfLinks = [];
    let imageList = "";
    const [isRendered,setIsRendered] = useState(false);

    const { isConnected } = useSelector((state) => state.isConnected);

    useEffect(() => {if(!isRendered){ReadLinks();}}, [isConnected]);

    const ReadLinks = () => {
        if(isConnected){
            contractModel.getAllLinks().then((_listOfLinks) => {
                listOfLinks = _listOfLinks;
                setIsRendered(true);

            });
            console.log(listOfLinks[0]);


        }

    }

    if(isRendered){
        return(
            <>
                <p className="secondaryText">My Nfts:</p>
                <p className="errorMessage">*Connect to Metamask First*</p>
            </>
        );
    }
    else{
        return(
            <>
                <p className="secondaryText">My Nfts:</p>
                {listOfLinks.map((link) => {
                    return <img classname="myNftImage" src="link"></img>;
                })}
            </>
        );
    }


}
export default MyNfts;
