import { useState, useEffect } from "react";
import { Box, TextField, Button } from '@mui/material';
import { Container, Typography } from "@mui/material";
import Mint  from "./Mint";  
import axios from 'axios';

//!add my nfts site!
//!make it look acceptable!

function App(){
    const [generated, setGenerated] = useState(false);
    const [message, setMessage] = useState("");
    const [nftParameters, setNftParameters] = useState("");
    const [imageLink, setImageLink] = useState("");

    useEffect(() => {console.log("useEffect")}, [generated, message, imageLink]);

    const generateNft = async () => {
        console.log("working");
        const link = await axios.get(`http://localhost:3001/?aiParams=${nftParameters}`);
        console.log("done");
        return(link.data);
    }

    const handleChange = (e) =>{
        console.log(e.target.value);
        setNftParameters(e.target.value);
    }


    const handleSubmit = () => {
        if(nftParameters == ""){
            setMessage("*At least 1 word*");
        }
        else{
            setMessage("*Generatig in progress...*")
            generateNft().then((data) => {
                console.log("xd");
                setImageLink(data); 
            });
            setGenerated(true);
            //setMessage("*failed to generate*");
        }
    }

    const handleMint = () => {
        //contract model mint()
    }

    if(imageLink != ""){
        return (
            <Container>
                <Typography sx={{fontFamily:"Roboto", mt:16, textAlign: "center", fontWeight: 700, color: "primary.main", border: "1 solid primary.dark", fontSize: 80 }} >
                    Mint your ideas!
                </Typography> 
                <Typography sx={{fontFamily:"Roboto", color:"primary.dark", fontSize:30, mt:0, textAlign:"center" }}>
                    Ai generated Nfts
                </Typography>
                <Box sx={{mt:10}}>
                    <TextField onChange={handleChange} sx={{width:400, boxShadow: "0px 7px 4px rgba(0, 0, 0, 0.25)",}}></TextField>
                    <Button onClick={handleSubmit} sx={{}}>Generate</Button>
                </Box>
            </Container>
            );
    }
    else{
        return(
            <Mint link={imageLink}/>
        );

    }

}

export default App;
