import { Box, Container, createTheme, ThemeProvider, Typography, Button, Link } from "@mui/material";
import { useState, useEffect} from "react";

const Header = () => {
    const [connected, setConnected] = useState(false);
    let button = <Button sx={{bgcolor:"#e1751a", color:"primary.main", '&:hover': {backgroundColor:"#FF8C00"} }} onClick={handleMetamask()}>Connect To Metamask</Button>;

    const handleMetamask = () =>{
        if(connected){
            //dissconnect from metamask from contract model
            setConnected(false)
        }
        else{
            //connect to metamask from contract model
            setConnected(true)
        }
    }

    useEffect(() => {if(connected){
        button = <Button sx={{bgcolor:"#e1751a", color:"primary.main", '&:hover': {backgroundColor:"#FF8C00"} }} onClick={handleMetamask()}>Connect To Metamask</Button>
    } else{
        button = <Button sx={{bgcolor:"gray", color:"primary.main", '&:hover': {backgroundColor:"primary.main"} }} onClick={handleMetamask()}>Disconnect From Metamask</Button>
    }}, connected);

    return(
        <Box m={-1} height={70} sx={{bgcolor:"primary.dark",alignItems:"flex-end"}} justifyContent="flex-end">
            <Link href="/"></Link>
            {button}
        </Box>
    );
}
export default Header;
