import { useState, useEffect } from "react";
import { Box, TextField, Button } from '@mui/material';
import { Container, Typography } from "@mui/material";  
import { Contract } from "ethers";
const contractModel = require('./contractModel.js');

require("./contractModel.js");

//!add my nfts site!
//!make it look acceptable!

function App(){
    const [generated, setGenerated] = useState(false);
    const [message, setMessage] = useState("");
    const [nftParameters, setNftParameters] = useState("");
    const [imageLink, setImageLink] = useState("");

    useEffect(() => {console.log("useEffect")}, [generated, message, imageLink]);

    const handleChange = (value) =>{
        setNftParameters(value);
    }

    const handleSubmit = async () => {
        if(nftParameters == ""){
            setMessage("*At least 1 word*");
        }
        else{
            setMessage("*Generatig in progress...*")
            contractModel
            setGenerated(true);
            //else 
            setMessage("*failed to generate*");
        }
    }

    const handleMint = () => {
        //contract model mint()
    }

    if(!generated){
        return (
            <Container>
                <Typography sx={{fontFamily:"Roboto", mt:16, textAlign: "center", fontWeight: 700, color: "primary.main", border: "1 solid primary.dark", fontSize: 100 }} >
                    Mint your ideas!
                </Typography> 
                <Typography sx={{fontFamily:"Roboto", color:"primary.dark", fontSize:30, mt:0, textAlign:"center" }}>
                    Ai generated Nfts
                </Typography>
                <TextField onChange={handleChange} sx={{width:400, left:250, boxShadow: "0px 7px 4px rgba(0, 0, 0, 0.25)", mt: 5, }}></TextField>
                <Button onClick={handleSubmit}>Generate</Button>
            </Container>
            );
    }
    else{
        return (
            <Container>
                <image href={imageLink}></image>
                <Button onClick={handleMint()} sx={{}}>Mint</Button>
            </Container>
        );

    }

}

export default App;
