    }

    if(!generated){
        return (
            <Container>
                <Typography sx={{fontFamily:"Roboto", mt:16, textAlign: "center", fontWeight: 700, color: "primary.main", border: "1 solid primary.dark", fontSize: 80 }} >
                    Mint your ideas!
                </Typography> 
                <Typography sx={{fontFamily:"Roboto", color:"primary.dark", fontSize:30, mt:0, textAlign:"center" }}>
                    Ai generated Nfts
                </Typography>
                <Box sx={{mt:10}}>
                    <TextField onChange={handleChange(value)} sx={{width:400, boxShadow: "0px 7px 4px rgba(0, 0, 0, 0.25)",}}></TextField>
                    <Button onClick={handleSubmit()} sx={{}}>Generate</Button>
                </Box>
            </Container>
            );
    }
    else{
        return (
            <Container>
                <img href={imageLink}/>
                <Button onClick={handleMint()} sx={{}}>Mint</Button>
            </Container>
        );

    }

}

export default App;
