import { useState, useEffect } from "react";
import axios from 'axios';
import "./index.css";
import { useSelector } from "react-redux";
const contractModel = require('./contractModel.js');

function App(){

    const [generated, setGenerated] = useState(false);
    const [message, setMessage] = useState("");
    const [nftParameters, setNftParameters] = useState("");
    const [imageLink, setImageLink] = useState("");
    const [loadingText, setLoadingText] = useState("Your Nft is being Generated...");

    useEffect(() => {console.log("useEffect")}, [imageLink,generated, message,loadingText]);

    const {isConnected} = useSelector((state) => state.isConnected.isConnected)

    const generateNft = async () => {
        console.log("working");
        const link = await axios.get(`http://localhost:3001/?aiParams=${nftParameters}`);
        console.log("done");
        return(link.data);
    }

    const handleChange = (e) =>{
        console.log(e.target.value);
        setNftParameters(e.target.value);
    }


    const handleSubmit = () => {
        if(nftParameters == ""){
            setMessage("*At least 1 word*");
        }
        else{
            setMessage("*Generatig in progress...*")
            generateNft().then((data) => {
                loadingText = "Your ai generatedNft is ready";
                console.log("xd");
                setImageLink(data); 
            });
            setGenerated(true);
        }
    }

    const handleMint = () => {
        if(isConnected){
            contractModel.mint(imageLink);
        }
        else{
            setLoadingText("Connect your wallet first!");
        }

    }
    if(!generated){
        return (
            <div>
                <p className="mainText">
                    Mint your ideas!
                </p> 
                <p className="subText">
                    Ai generated Nfts
                </p>
                <div className="generateBar">
                    <input onChange={handleChange} className="textInput" placeholder="Eg. Bored monkey wearing a rolex"></input>
                    <button onClick={handleSubmit} className="generateButton"><img src="../UI/button4.png"/></button>
                </div>
            </div>
            );
    }
    else{
        return(
            <div>
                <p className="generatedText">{loadingText}</p>
                <img className="imageNft" src={imageLink}/>
                <button className="mintButton" onClick={handleMint()}><img src={imageLink?"../UI/Button3.png":""}/></button>
            </div>
        );

    }

}

export default App;
