const ethers = require('ethers');
const abi = require('./artifacts/contracts/AiNft.sol/AiNft.json');

let network = "goerli";
let currentAccount = "";
let provider;
let signer;
let contractAddress = "0xA669F6F1Cb417A13b16D80fc714d191f42783Cae";
let infuraUrl = "https://goerli.infura.io/v3/5bf64147f23b4d0593632928be0061c3";

exports.connectToMetamask = async () => {
    if(window.ethereum) {
        try{
            const accounts = await window.ethereum.request({
                method: "eth_requestAccounts",
            });
            currentAccount = accounts[0];
            provider = new ethers.providers.JsonRpcProvider(infuraUrl);
            const signer = new ethers.providers.Web3Provider(window.ethereum).getSigner();
            console.log("connected");

            const aiNftContract = new ethers.Contract(contractAddress,abi.abi,provider);

            /*
            aiNftContract.balanceOf(currentAccount).then((value) => {
                console.log(value);
            });
            */

            //change the status to connected!!!
            const options = {value: ethers.utils.parseEther("0")};
            const response = await aiNftContract.connect(signer).mint("https://oaidalleapiprodscus.blob.core.windows.net/private/org-5gnnVMN9evmiHZo4UfpYT6VK/user-bFeLzoY1IgYHbHhjNOsfJEBq/img-PvnzZAkg8ITijETgUH5EbYbl.png?st=2023-02-24T14%3A55%3A57Z&se=2023-02-24T16%3A55%3A57Z&sp=r&sv=2021-08-06&sr=b&rscd=inline&rsct=image/png&skoid=6aaadede-4fb3-4698-a8f6-684d7786b067&sktid=a48cca56-e6da-484e-a814-9c849652bcb3&skt=2023-02-24T02%3A55%3A45Z&ske=2023-02-25T02%3A55%3A45Z&sks=b&skv=2021-08-06&sig=5mohCc3aMmSnwY%2BLmwYArXebonBf5Q%2Bqe%2BGVhGrGy%2BI%3D",options);
            const events  = await response.wait();
            console.log(events.events[0]);
        }
        catch(error){
            console.log(error);
        }
    }
}

exports.mint = async (imageLink) => {
    try{
        if(provider&&abi.abi&&currentAccount){
            const aiNftContract = new ethers.Contract(contractAddress,abi.abi,provider);
            const options = {value: ethers.utils.parseEther("0")};
            const response = await aiNftContract.connect(signer).mint(options, imageLink);
            const events  = await response.wait();
            console.log(events.events[0]);
            return true
        }
    }
    catch(err){
        return false;
    }        
}
exports.getAllLinks = async () => {
    let srcList = [];
    try{
        if(provider&&abi.abi&&currentAccount){
            const aiNftContract = new ethers.Contract(contractAddress,abi.abi,provider);
            const response = await aiNftContract.getOwnedNFTIds(currentAccount);
            //check how to read returned arrays! 
            for(let i = 0; i < response.length; i++){
                srcList.push(await aiNftContract.getUrl(response[i]));
            }
            console.log(srcList);
            return srcList;

        }
        else{
            throw false;
        }
    }
    catch(err){
        console.log(err);
        return false;
    }


}
